import React, { useEffect, useState } from "react";
import { FileUploader } from "react-drag-drop-files";
import logo from "./logo.svg";
import "./App.css";
import readFile from "./utils/readFile";
import { createMap } from "./utils/createMap";
import RenderMap from "./utils/RenderMap";
import { Hero } from "./type/type";
import { onMove } from "./utils/onMove";

const App = () => {
  const [content, setContent] = useState<string[]>([]);
  const [map, setMap] = useState<string[][]>([]);
  const [heroes, setHeroes] = useState<Hero[]>([]);

  useEffect(() => {
    if (content.length > 0) {
      const { map, heroes } = createMap(content);
      setMap(map);
      setHeroes(heroes);
    }
  }, [content]);

  const onSubmitFile = (file: any) => {
    const reader = readFile(file);
    if (reader) {
      reader.onload = () => {
        const tmp = reader.result as string;
        setContent(tmp.split("\n"));
      };
    } else {
      alert("error");
    }
  };

  const onClickStart = () => {
    let tmpMap = [...map];
    heroes.forEach((hero) => {
      let currentOrientation = hero.orientation;
      let currentPos = hero.position;
      hero.moves.forEach((move) => {
        const { map, position, orientation } = onMove(
          tmpMap,
          currentOrientation,
          currentPos,
          move
        );
        tmpMap = map;
        currentOrientation = orientation;
        currentPos = position;
      });
    });
    setMap(tmpMap);
  };

  console.log(map);
  return (
    <div className="App">
      {map.length > 0 ? (
        <>
          <RenderMap map={map} />
          <button onClick={onClickStart}>start</button>
        </>
      ) : (
        <>
          <img src={logo} className="App-logo" alt="logo" />

          <FileUploader
            handleChange={onSubmitFile}
            name="file"
            types={["TXT"]}
          />
        </>
      )}
    </div>
  );
};

export default App;
