import { MOOVE, ORIENTATION } from "../type/enum";
import { Hero, Pos } from "../type/type";

const getPrevCase = (current: string) => {
  let newPrevCase: string = current;
  if (current.includes("T")) {
    if (current === "T") newPrevCase = "0";
    else {
      const strNumber = current.slice(1);
      const rest = +strNumber - 1;
      newPrevCase = `T${rest}`;
    }
  }
  return newPrevCase;
};

const changeOrientation = (move: MOOVE, currentOrientation: ORIENTATION) => {
  let orientation = currentOrientation;
  if (move === MOOVE.GAUCHE) {
    if (currentOrientation === ORIENTATION.NORD)
      orientation = ORIENTATION.OUEST;
    else if (currentOrientation === ORIENTATION.SUD)
      orientation = ORIENTATION.EST;
    else if (currentOrientation === ORIENTATION.EST)
      orientation = ORIENTATION.NORD;
    else if (currentOrientation === ORIENTATION.OUEST)
      orientation = ORIENTATION.SUD;
  } else if (move === MOOVE.DROITE) {
    if (currentOrientation === ORIENTATION.NORD) orientation = ORIENTATION.EST;
    else if (currentOrientation === ORIENTATION.SUD)
      orientation = ORIENTATION.OUEST;
    else if (currentOrientation === ORIENTATION.EST)
      orientation = ORIENTATION.SUD;
    else if (currentOrientation === ORIENTATION.OUEST)
      orientation = ORIENTATION.NORD;
  }
  return orientation;
};

const moveHero = (
  hero: Hero,
  currentMap: string[][]
): { map: string[][]; position: Pos; prevCase: string } => {
  let mapTmp = [...currentMap];
  const {
    position: currentPosition,
    orientation: currentOrientation,
    prevCase: currentPrevCase,
    name,
  } = hero;
  const defaultReturn = {
    map: currentMap,
    position: currentPosition,
    prevCase: currentPrevCase,
  };
  let newPrevCase: string;

  if (currentOrientation === ORIENTATION.NORD) {
    if (!mapTmp[currentPosition.x - 1]) return defaultReturn;
    if (!["0", "T"].includes(mapTmp[currentPosition.x - 1][currentPosition.y]))
      return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = currentPrevCase;
    newPrevCase = getPrevCase(mapTmp[currentPosition.x - 1][currentPosition.y]);
    mapTmp[currentPosition.x - 1][currentPosition.y] = name;

    return {
      map: mapTmp,
      position: { x: currentPosition.x - 1, y: currentPosition.y },
      prevCase: newPrevCase,
    };
  } else if (currentOrientation === ORIENTATION.SUD) {
    if (!mapTmp[currentPosition.x + 1]) return defaultReturn;
    if (!["0", "T"].includes(mapTmp[currentPosition.x + 1][currentPosition.y]))
      return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = currentPrevCase;
    newPrevCase = getPrevCase(mapTmp[currentPosition.x + 1][currentPosition.y]);
    mapTmp[currentPosition.x + 1][currentPosition.y] = name;

    return {
      map: mapTmp,
      position: { x: currentPosition.x + 1, y: currentPosition.y },
      prevCase: newPrevCase,
    };
  } else if (currentOrientation === ORIENTATION.EST) {
    if (!mapTmp[currentPosition.x][currentPosition.y + 1]) return defaultReturn;
    if (!["0", "T"].includes(mapTmp[currentPosition.x][currentPosition.y + 1]))
      return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = currentPrevCase;
    newPrevCase = getPrevCase(mapTmp[currentPosition.x][currentPosition.y + 1]);
    mapTmp[currentPosition.x][currentPosition.y + 1] = name;

    return {
      map: mapTmp,
      position: { x: currentPosition.x, y: currentPosition.y + 1 },
      prevCase: newPrevCase,
    };
  } else if (currentOrientation === ORIENTATION.OUEST) {
    if (!mapTmp[currentPosition.x][currentPosition.y - 1]) return defaultReturn;
    if (!["0", "T"].includes(mapTmp[currentPosition.x][currentPosition.y - 1]))
      return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = currentPrevCase;
    newPrevCase = getPrevCase(mapTmp[currentPosition.x][currentPosition.y - 1]);
    mapTmp[currentPosition.x][currentPosition.y - 1] = name;

    return {
      map: mapTmp,
      position: { x: currentPosition.x, y: currentPosition.y - 1 },
      prevCase: newPrevCase,
    };
  }

  return defaultReturn;
};

export const onMove = (
  currentMap: string[][],
  hero: Hero,
  move: MOOVE
): {
  map: string[][];
  newHeroStat: Hero;
} => {
  if (move === MOOVE.AVANCE) {
    const { map, position, prevCase } = moveHero(hero, currentMap);
    return {
      map: map,
      newHeroStat: { ...hero, position, prevCase },
    };
  } else if (move === MOOVE.DROITE || move === MOOVE.GAUCHE) {
    const orientation = changeOrientation(move, hero.orientation);
    return {
      map: currentMap,
      newHeroStat: { ...hero, orientation },
    };
  }
  return {
    map: currentMap,
    newHeroStat: { ...hero },
  };
};
