import React from "react";
import { isNumeric } from "./isNumeric";

interface Pos {
  x: number;
  y: number;
}

const checkLine = (line: string[]) => {
  if ((line[0] = "C")) {
    if (line.length > 3) alert("error map lenght");
    if (!isNumeric(line[1]) || !isNumeric(line[2])) alert("error map number");
  }
  if ((line[0] = "T")) {
    if (line.length > 3) alert("error treasure lenght");
    if (!isNumeric(line[1]) || !isNumeric(line[2]) || !isNumeric(line[3]))
      alert("error treasure number");
  }
  if ((line[0] = "M")) {
    if (line.length > 3) alert("error mountain lenght");
    if (!isNumeric(line[1]) || !isNumeric(line[2]))
      alert("error mountain number");
  }
};

