import React from "react";
import { MOOVE, ORIENTATION } from "../type/enum";
import { Pos } from "../type/type";

const changeOrientation = (move: MOOVE, currentOrientation: ORIENTATION) => {
  let orientation = currentOrientation;
  if (move === MOOVE.GAUCHE) {
    if (currentOrientation === ORIENTATION.NORD)
      orientation = ORIENTATION.OUEST;
    else if (currentOrientation === ORIENTATION.SUD)
      orientation = ORIENTATION.EST;
    else if (currentOrientation === ORIENTATION.EST)
      orientation = ORIENTATION.NORD;
    else if (currentOrientation === ORIENTATION.OUEST)
      orientation = ORIENTATION.SUD;
  } else if (move === MOOVE.DROITE) {
    if (currentOrientation === ORIENTATION.NORD) orientation = ORIENTATION.EST;
    else if (currentOrientation === ORIENTATION.SUD)
      orientation = ORIENTATION.OUEST;
    else if (currentOrientation === ORIENTATION.EST)
      orientation = ORIENTATION.SUD;
    else if (currentOrientation === ORIENTATION.OUEST)
      orientation = ORIENTATION.NORD;
  }
  return orientation;
};

const moveHero = (
  currentPosition: Pos,
  currentOrientation: ORIENTATION,
  currentMap: string[][]
): { map: string[][]; position: Pos } => {
  let mapTmp = [...currentMap];
  const defaultReturn = { map: currentMap, position: currentPosition };

  if (currentOrientation === ORIENTATION.NORD) {
    if (!mapTmp[currentPosition.x - 1]) return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = "0";
    mapTmp[currentPosition.x - 1][currentPosition.y] = "A";

    return {
      map: mapTmp,
      position: { x: currentPosition.x - 1, y: currentPosition.y },
    };
  } else if (currentOrientation === ORIENTATION.SUD) {
    if (!mapTmp[currentPosition.x + 1]) return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = "0";
    mapTmp[currentPosition.x + 1][currentPosition.y] = "A";

    return {
      map: mapTmp,
      position: { x: currentPosition.x + 1, y: currentPosition.y },
    };
  } else if (currentOrientation === ORIENTATION.EST) {
    if (!mapTmp[currentPosition.x][currentPosition.y + 1]) return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = "0";
    mapTmp[currentPosition.x][currentPosition.y + 1] = "A";

    return {
      map: mapTmp,
      position: { x: currentPosition.x, y: currentPosition.y + 1 },
    };
  } else if (currentOrientation === ORIENTATION.OUEST) {
    if (!mapTmp[currentPosition.x][currentPosition.y - 1]) return defaultReturn;

    mapTmp[currentPosition.x][currentPosition.y] = "0";
    mapTmp[currentPosition.x][currentPosition.y - 1] = "A";

    return {
      map: mapTmp,
      position: { x: currentPosition.x, y: currentPosition.y - 1 },
    };
  }

  return { map: currentMap, position: currentPosition };
};

export const onMove = (
  currentMap: string[][],
  currentOrientation: ORIENTATION,
  currentPosition: Pos,
  move: MOOVE
): { map: string[][]; position: Pos; orientation: ORIENTATION } => {
  if (move === MOOVE.AVANCE) {
    const { map, position } = moveHero(
      currentPosition,
      currentOrientation,
      currentMap
    );
    return { map: map, position: position, orientation: currentOrientation };
  } else if (move === MOOVE.DROITE || move === MOOVE.GAUCHE) {
    const orientation = changeOrientation(move, currentOrientation);
    return {
      map: currentMap,
      position: currentPosition,
      orientation: orientation,
    };
  }
  return {
    map: currentMap,
    position: currentPosition,
    orientation: currentOrientation,
  };
};
